#!/bin/bash
yum update -y
rm /etc/yum.repos.d/mongodb.repo
mkdir -p /data/db
echo '[mongodb]' >>  /etc/yum.repos.d/mongodb.repo
echo 'name=MongoDB Repository' >>  /etc/yum.repos.d/mongodb.repo
echo 'baseurl=http://downloads-distro.mongodb.org/repo/redhat/os/x86_64/' >>  /etc/yum.repos.d/mongodb.repo
echo 'gpgcheck=0' >>  /etc/yum.repos.d/mongodb.repo
echo 'enabled=1' >>  /etc/yum.repos.d/mongodb.repo
echo "/etc/yum.repos.d/mondgob.repo created"
yum install -y mongo-10gen mongo-10gen-server
sed -i 's/bind_ip=127.0.0.1/bind_ip=0.0.0.0/' /etc/mongod.conf
service mongod start
echo 'Mongodb Installed'
curl --silent --location https://rpm.nodesource.com/setup_8.x | bash -
yum install -y nodejs
echo 'Node.JS Installed'
mkdir -p /home/ec2-user/public
cat > /home/ec2-user/public/index.html <<END
<!DOCTYPE html>
<html ng-app='app'>
  <head>
    <meta charset='utf-8'>
    <title>MEAN Stack app</title>
  </head>
  <body ng-controller='MainCtrl'>
    {{message}}
  </body>
  <script src='https://ajax.googleapis.com/ajax/libs/angularjs/1.5.6/angular.min.js' charset='utf-8'></script>
  <script type='text/javascript'>
    angular.module('app',[])
      .controller('MainCtrl', function(\$scope){
        \$scope.message = 'Hello from mlabouardy ^^'
      })
  </script>
</html>
END
cat > /home/ec2-user/package.json <<END
{
  "name": "simple-app",
  "authors": "Mohamed Labouardy",
  "dependencies": {
    "express": "^4.15.4",
    "mongodb": "^2.2.31"
  }
}
END
npm install --prefix /home/ec2-user/
cat > /home/ec2-user/server.js <<END
var express = require('express'),
    app = express(),
    MongoClient = require("mongodb").MongoClient,
    database = null,
    port = process.env.PORT || 3000;
var movies = [
  {
    name: "Spider-Man: Homecoming"
  },
  {
    name: "Dunkirk"
  },
  {
    name: "Despicable Me 3"
  }
]
MongoClient.connect("mongodb://localhost/mydb", function(error, db) {
  db.collection('movies').insert(movies, function(err, res) {
    database = db
    console.log('saved to database')
  })
})
app.use(express.static(__dirname + '/public'));
app.use('/api', function(req, res){
  database.collection('movies').find().toArray(function(err, data){
    res.json(data)
  })
})
app.listen(port, function(){
  console.log('Server listening on port ', port)
})
END
nohup node /home/ec2-user/server.js > app.out 2> app.err < /dev/null &
